import { Card, CardBody, CardImg, CardTitle, Col, Row } from "reactstrap";
import Link from "next/link"
import { gql } from "apollo-boost"
import { useQuery } from "@apollo/react-hooks"
import { useRouter } from "next/router";

const GET_RESTAURANT_DISHES = gql `
  query ($id : ID!) {
    restaurant(id: $id) {
      id
      name
      dishes {
        id
        name
        description
        price
        image {
          url
        }
      }
    }
  } 
`;

const Restaurants = (props) => {
  const router = useRouter()
  const {loading, error, data} = useQuery(GET_RESTAURANT_DISHES, {
    variables: {id: router.query.id},
  });
  if(error) return "レストランの読み込みに失敗しました。"
  if(loading) return <h1>読み込み中...</h1>
  if (data) {
    const restaurants = data
    return (
      <Row>
        {restaurants?.restaurant?.dishes?.map((res) => (
          <>
          <Col xs="6" sm="4" key={res.id} style ={{padding: 0}}>
            <Card style={{margin:"0.05rem 20px 0.5rem"}}>
              <CardImg 
                src={`${process.env.NEXT_PUBLIC_API_URL}${res?.image.url}`} 
                top={true} 
                style={{height: 250}}
              />
              <CardBody>
                <CardTitle>{res.name}</CardTitle>
                <CardTitle>{res.description}</CardTitle>
              </CardBody>  
              <div className="card-footer">
                <Link 
                  as={`/restaurants/${res.id}`}
                  href={`/restaurants?id=${res.id}`}
                >
                    <a className="btn btn-primary">もっと見る</a>
                </Link>
              </div>
            </Card>
          </Col>  
          </>
        ))}

        <style jsx>
          {`
          a {
            color: white;
          }
          a:linl {
            text-decoation: none;
            color: white;
          }
          .card-colums {
            column-connt: 3;
          }
          `}
        </style>
      </Row>
      ) 
  } else return <h1>レストランが見つかりませんでした。</h1>
}
export default Restaurants;
