const query = gql `
  {
    restaurants {
      id 
      name 
      description
      image{
        url
      }
    }
  }`

const RestaurantsList = () => {
  const {loading, error, data} = useQuery(query);

  if(data.restaurants )
  return (
  <Row>
    <Col xs="6" sm="4" >
